Если ТипЗнч(Объект) = Тип("Структура") Тогда
Если ЗначениеЗаполнено(Объект.Получатель) Тогда
	Если ТипЗнч(Объект.Получатель) = Тип("СправочникСсылка.КонтактныеЛицаПартнеров") Тогда
		Обращение = "Уважаемый ";
		Если Объект.Получатель.Пол = Перечисления.ПолФизическогоЛица.Женский Тогда
			Обращение = "Уважаемой ";
		ИначеЕсли Объект.Получатель.Пол = Перечисления.ПолФизическогоЛица.Мужской Тогда
			Обращение = "Уважаемому ";
		КонецЕсли;
		Стр = СокрЛП(Объект.Получатель.CRM_Фамилия) + " " + СокрЛП(Объект.Получатель.CRM_Имя) + " " + СокрЛП(Объект.Получатель.CRM_Отчество);
		Результат = Обращение + CRM_ОбщегоНазначенияСервер.Склонение(Стр, "Д", Объект.Получатель.Пол);
	Иначе
		Результат = СокрЛП(Объект.Получатель.Наименование);
	КонецЕсли;	
Иначе
	Результат = "";
КонецЕсли;
Иначе
