v8:item.v8:content:
Если ЗначениеЗаполнено(Объект.КонтактноеЛицо) Тогда
	Результат = СокрЛП(Объект.КонтактноеЛицо.CRM_Имя);
ИначеЕсли ТипЗнч(Объект.Партнер) = Тип("СправочникСсылка.Партнеры")
И Объект.Партнер.ЮрФизЛицо = Перечисления.КомпанияЧастноеЛицо.ЧастноеЛицо Тогда
	Результат = СокрЛП(Объект.Партнер.CRM_Имя);
Иначе
	Результат = СокрЛП(Объект.Партнер.Наименование);
КонецЕсли;

Если ЗначениеЗаполнено(Объект.КонтактноеЛицо) Тогда
	Результат = СокрЛП(Объект.КонтактноеЛицо.CRM_Имя);
ИначеЕсли ТипЗнч(Объект.Партнер) = Тип("СправочникСсылка.Партнеры")
И Объект.Партнер.ЮрФизЛицо = Перечисления.КомпанияЧастноеЛицо.ЧастноеЛицо Тогда
	Результат = СокрЛП(Объект.Партнер.CRM_Имя);
Иначе
	Результат = СокрЛП(Объект.Партнер.Наименование);
КонецЕсли;

Если ЗначениеЗаполнено(Объект.КонтактноеЛицо) Тогда
	Результат = СокрЛП(Объект.КонтактноеЛицо.CRM_Имя)+" "+СокрЛП(Объект.КонтактноеЛицо.CRM_Отчество);
ИначеЕсли ТипЗнч(Объект.Партнер) = Тип("СправочникСсылка.Партнеры")
И Объект.Партнер.ЮрФизЛицо = Перечисления.КомпанияЧастноеЛицо.ЧастноеЛицо Тогда
	Результат = СокрЛП(Объект.Партнер.CRM_Имя)+" "+СокрЛП(Объект.Партнер.CRM_Отчество);
Иначе
	Результат = СокрЛП(Объект.Партнер.Наименование);
КонецЕсли;

Если ЗначениеЗаполнено(Объект.КонтактноеЛицо) Тогда
	Результат = СокрЛП(Объект.КонтактноеЛицо.CRM_Имя)+" "+СокрЛП(Объект.КонтактноеЛицо.CRM_Отчество);
ИначеЕсли ТипЗнч(Объект.Партнер) = Тип("СправочникСсылка.Партнеры")
И Объект.Партнер.CRM_ВидПартнера = Перечисления.CRM_ВидПартнера.ЧастноеЛицо Тогда
	Результат = СокрЛП(Объект.Партнер.CRM_Имя)+" "+СокрЛП(Объект.Партнер.CRM_Отчество);
Иначе
	Результат = СокрЛП(Объект.Партнер.Наименование);
КонецЕсли;

Если ЗначениеЗаполнено(Объект.КонтактноеЛицо) Тогда
		Результат = СокрЛП(Объект.КонтактноеЛицо.Наименование);
Иначе
	Результат = СокрЛП(Объект.Партнер.Наименование);
КонецЕсли;

Если ЗначениеЗаполнено(Объект.КонтактноеЛицо) Тогда
		Результат = СокрЛП(Объект.КонтактноеЛицо.Наименование);
Иначе
	Результат = СокрЛП(Объект.Партнер.Наименование);
КонецЕсли;

Если ЗначениеЗаполнено(Объект.КонтактноеЛицо) Тогда
	Обращение = "Уважаемый ";
	Если Объект.КонтактноеЛицо.Пол = Перечисления.ПолФизическогоЛица.Женский Тогда
		Обращение = "Уважаемой ";
	ИначеЕсли Объект.КонтактноеЛицо.Пол = Перечисления.ПолФизическогоЛица.Мужской Тогда
		Обращение = "Уважаемому ";
	Иначе
		Обращение = "Уважаемому(ой) ";
	КонецЕсли;
	Стр = СокрЛП(Объект.КонтактноеЛицо.CRM_Фамилия) + " " + СокрЛП(Объект.КонтактноеЛицо.CRM_Имя) + " " 
	+ СокрЛП(Объект.КонтактноеЛицо.CRM_Отчество);
	Результат = Обращение + CRM_ОбщегоНазначенияСервер.Склонение(Стр, "Д", Объект.КонтактноеЛицо.Пол);
ИначеЕсли ТипЗнч(Объект.Партнер) = Тип("СправочникСсылка.Партнеры")
И Объект.Партнер.ЮрФизЛицо = Перечисления.КомпанияЧастноеЛицо.ЧастноеЛицо Тогда
	Если Объект.Партнер.Пол = Перечисления.ПолФизическогоЛица.Женский Тогда
		Обращение = "Уважаемой ";
	ИначеЕсли Объект.Партнер.Пол = Перечисления.ПолФизическогоЛица.Мужской Тогда
		Обращение = "Уважаемому ";
	Иначе
		Обращение = "Уважаемому(ой) ";
	КонецЕсли;
	Стр = СокрЛП(Объект.Партнер.CRM_Фамилия)+" "+СокрЛП(Объект.Партнер.CRM_Имя) + " " 
	+ СокрЛП(Объект.Партнер.CRM_Отчество);
	Результат = Обращение + CRM_ОбщегоНазначенияСервер.Склонение(Стр, "Д", Объект.Партнер.Пол);
ИначеЕсли ТипЗнч(Объект.Партнер) = Тип("СправочникСсылка.ФизическиеЛица") Тогда
	Если Объект.Партнер.Пол = Перечисления.ПолФизическогоЛица.Женский Тогда
		Обращение = "Уважаемой ";
	ИначеЕсли Объект.Партнер.Пол = Перечисления.ПолФизическогоЛица.Мужской Тогда
		Обращение = "Уважаемому ";
	КонецЕсли;
	Стр = СокрЛП(Объект.Партнер.Наименование);
	Результат = Обращение + Стр;
Иначе
	Результат = СокрЛП(Объект.Партнер.Наименование);
КонецЕсли;

